#include <gtest/gtest.h>

using namespace boost::text;

auto const error = [](std::string const & s) { std::cout << s; };
auto const warning = [](std::string const & s) {};

collation_table make_save_load_table()
{
#ifdef LIMIT_TESTING_FOR_CI
    std::string const table_str(data::ee::standard_collation_tailoring());
    return tailored_collation_table(
        table_str,
        "ee::standard_collation_tailoring()", error, warning);
#else
    if (!exists(boost::filesystem::path("ee_standard.table"))) {
        std::string const table_str(data::ee::standard_collation_tailoring());
        collation_table table = tailored_collation_table(
            table_str,
            "ee::standard_collation_tailoring()", error, warning);
        save_table(table, "ee_standard.table.0");
        boost::filesystem::rename("ee_standard.table.0", "ee_standard.table");
    }
    return load_table("ee_standard.table");
#endif
}
collation_table const & table()
{
    static collation_table retval = make_save_load_table();
    return retval;
}
