collation_table make_save_load_table()
{
#ifdef LIMIT_TESTING_FOR_CI
    std::string const table_str(data::he::search_collation_tailoring());
    return tailored_collation_table(
        table_str,
        "he::search_collation_tailoring()", error, warning);
#else
    if (!exists(boost::filesystem::path("he_search.table"))) {
        std::string const table_str(data::he::search_collation_tailoring());
        collation_table table = tailored_collation_table(
            table_str,
            "he::search_collation_tailoring()", error, warning);
        save_table(table, "he_search.table.0");
        boost::filesystem::rename("he_search.table.0", "he_search.table");
    }
    return load_table("he_search.table");
#endif
}
